INSERT INTO `modx_site_snippets` VALUES (1,0,0,'BackupMODXWidget','Backup MODX Dashboard widget',0,1,0,'/**\n * BackupMODX\n *\n * MODX Backup Dashboard Widget\n *\n *\n * @package backupmodx\n */\n \n\n// Returns an empty string if user shouldn\'t see the widget\n$groups = $modx->getOption(\'groups\', $scriptProperties, \'Administrator\', true);\nif (strpos($groups, \',\') !== false) {\n	$groups = explode(\',\', $groups);\n}\nif (!$modx->user->isMember($groups)) {\n	return \'\';\n} \n\n\n// Load Class\n$modelPath = $modx->getOption(\'core_path\', null, MODX_CORE_PATH) . \'components/backupmodx/model/backupmodx/\';\n$assetsUrl = $modx->getOption(\'assets_url\', null, MODX_ASSETS_URL);\n$assetsUrl = substr($assetsUrl, 1);\n$modx->loadClass(\'BackupMODX\', $modelPath, true, true);\n\n\n$backupMODX = new BackupMODX($modx, $config = array());\n\n\nif (isset($_POST[\'removebackupmodx\'])) {\n\n    $backup = $_SESSION[\'tmpActiveBackup\'];\n    $backupMODX->removeBackup($backup[\'filename\']);\n    \n    unset($_SESSION[\'tmpActiveBackup\']);\n}\n\nif (isset($_POST[\'restorebackupmodx\'])) {\n\n    $database = $_POST[\'database\'];\n    $restorebackup = $backupMODX->restoreBackup($database);\n    \n    if ($restorebackup === true) {\n        echo \'Backup was restored.\';\n        return;\n    }\n}\n\nreturn $modx->getChunk(\'backupMODXWidget\', array(\n    \'backup\' => is_array($_SESSION[\'tmpActiveBackup\'])? json_encode($_SESSION[\'tmpActiveBackup\']) : \'\',\n    \'assetsurl\' => $assetsUrl,\n));',0,NULL,'',0,''),(14,0,0,'cbFileFormatSize','Format a filesize. (Part of ContentBlocks)',0,20,0,'/**\n * $the cbFileFormatSize snippet is used for formatting a number of bytes, into a more user friendly format.\n * It is intended to be used as output filter. For example:\n *\n * [[+size:cbFileFormatSize]] where [[+size]] is an integer.\n *\n * Optionally, it is possible to specify the number of decimals that should be added. Do this by specifying\n * a numeric value as option, like this:\n *\n * [[+size:cbFileFormatSize=`0`]] => no decimals\n * [[+size:cbFileFormatSize=`2`]] => 2 decimals, which is also the default *\n *\n * @var modX $modx\n * @var array $input\n * @var array $options\n */\n\n$cbCorePath = $modx->getOption(\'contentblocks.core_path\', null, $modx->getOption(\'core_path\').\'components/contentblocks/\');\n$contentBlocks = $modx->getService(\'contentblocks\',\'ContentBlocks\', $cbCorePath.\'model/contentblocks/\');\n\n$bytes = $input;\n$decimals = (isset($options) && is_numeric($options)) ? $options : 2;\nreturn $contentBlocks->formatBytes($bytes, $decimals);',0,NULL,'',0,''),(15,2,0,'createUrl','',0,14,0,'/*\n * createUrl\n *\n * checks if the input is numeric and creates a url\n *\n * Usage examples:\n * [[*tvName:createUrl]]\n *\n * @author Jan Dähne <jan.daehne@quadro-system.de>\n */\n \nif (is_numeric($input)) {\n    return $modx->makeUrl($input);\n}else {\n    if (filter_var($input, FILTER_VALIDATE_EMAIL)) {\n        return \'mailto:\' . $input;\n    }\n    return $input;\n}',0,'a:0:{}','',0,''),(16,2,0,'eachTpl','',0,14,0,'/*\n * eachTpl\n *\n * templating a comma separted list of items to a chunk-template\n *\n * Usage examples:\n * [[*tvName:eachTpl=`chunkNameTpl`]]\n *\n * @author Jan Dähne <jan.daehne@quadro-system.de>\n */\n \n \n$items = explode(\',\', $input);\n$tpl = $options;\n$output = \'\';\n\nforeach ($items as $item) {\n    $output .= $modx->getChunk($tpl,array(\n        \'data\' => $item,\n    ));\n}\n\nreturn $output;',0,'a:0:{}','',0,''),(17,2,0,'embedVideoLink','',0,14,0,'/*\n * embedVideoLink\n *\n * Formats video Link of YouTube and Vimeo to embed Video-Link to use in Fancybox etc.\n *\n * Usage examples:\n * [[*tvName:embedVideoLink]]\n *\n * @author Jan Dähne <jan.daehne@quadro-system.de>\n */\n\n// YouTube\n$input = str_replace(\'youtu.be/\', \"youtube.com/embed/\", $input);\n$input = str_replace(\'youtube.com/watch?v=\', \"youtube.com/embed/\", $input);\n\n// Vimeo\n$input = str_replace(\'//vimeo.com/\', \"//player.vimeo.com/video/\", $input);\n\nreturn $input;',0,'a:0:{}','',0,''),(18,2,0,'exactDigits','',0,14,0,'/*\n * exactDigits\n *\n * adds leading zeros to numbers till it matches exact amount of chars\n *\n * Usage examples:\n * [[+tv:exactDigits=`5`]]\n *\n * @author Jan Dähne <jan.daehne@quadro-system.de>\n */\n\n// Properties \n$chars = $modx->getOption(\'options\', $scriptProperties, 2, true);\n\n// add zeros\nreturn sprintf(\'%0\'. $chars .\'d\', $input);',0,'a:0:{}','',0,''),(2,0,0,'UpgradeMODXWidget','Upgrade MODX Dashboard widget',0,5,0,'/**\n * UpgradeMODXWidget snippet for UpgradeMODX extra\n *\n * Copyright 2015-2018 Bob Ray <https://bobsguides.com>\n * Created on 08-16-2015\n *\n * UpgradeMODX is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * UpgradeMODX is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * UpgradeMODX; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package upgrademodx\n */\n\n/**\n * Description\n * -----------\n * UpgradeMODX Dashboard widget\n * This package was inspired by the work of a number of people and I have borrowed some of their code.\n * Dmytro Lukianenko (dmi3yy) is the original author of the MODX install script. Susan Sottwell, Sharapov,\n * Bumkaka, Inreti, Zaigham Rana, frischnetz, and AgelxNash, also contributed and I\'d like to thank all\n * of them for laying the groundwork.\n *\n * Variables\n * ---------\n * @var $modx modX\n * @var $scriptProperties array\n *\n * @package